void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = 20.0 * fragCoord.xy / iResolution.xx;

    float k = floor(pos.x) - floor(pos.y);

    if( mod(k , 2.0) < 0.1)
    	 fragColor = vec4(1.0, 1.0, 1.0, 1.0); 
    else
         fragColor = vec4(0.0, 0.0, 0.0, 1.0); 
}
