#define SPLAT


const vec3 UP = vec3(0.0,1.0,0.0);
const vec3 NE = vec3(0.01,0.0,0.0);

vec3 lightDir = normalize(vec3(1.0,2.0,-1.4));


float dm(in vec3 rp) {

    #ifdef SPLAT
    vec3 d = vec3(0.0);


    float powY = -pow(abs(rp.y),1.4);
    d.x += sign(rp.x)*powY;
    d.z += sign(rp.z)*powY;

    d.y += (sin(rp.x*5.0)+sin(rp.z*4.2))*0.4;

    #else

    vec3 d = vec3(10.0);

    for (float m = 2.0; m < 3.5; m+=.5) {
        d.x *= cos(rp.y*m+iTime*0.2)/m;
        d.y *= cos(rp.z*m+iTime)/m;
        d.z *= cos(rp.x*m)/m;
    }

    #endif

    return (length(rp+d)-1.0)*.4;
}
vec3 dn(in vec3 rp) {
    return normalize(vec3(dm(rp+NE)-dm(rp-NE),
                          dm(rp+NE.yxz)-dm(rp-NE.yxz),
                          dm(rp+NE.yzx)-dm(rp-NE.yzx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5);
    uv.y *= iResolution.y/iResolution.x;

	vec3 rp = vec3(sin(iTime*0.2)*6.0,sin(iTime*0.4)*3.0,cos(iTime*0.2)*8.0);
    vec3 rd = normalize(-rp);
    rd = normalize(uv.x*cross(rd,UP)+vec3(rd.x,rd.y+uv.y,rd.z));


    float s = 0.0,d;
    for (int i = 0; i < 256; i++) {
        d = dm(rp);
        if (d < 0.01) {
            //draw
            vec3 c = vec3(sin(rp.x*10.0)*0.5+0.5,0.0,sin(rp.z*5.0+iTime)*0.5+0.5);

            vec3 norm = dn(rp);
            float l = 0.3+max(0.0,dot(lightDir,norm));
            c = mix(c*l+vec3(pow(max(0.0,dot(reflect(lightDir,norm),rd)),8.0)*0.7),
                    texture(iChannel1,reflect(rd,norm)).xyz*l,0.16);

            fragColor = vec4(c,1.0);
            return;
        }

        s += d;
        if (s > 10.0) {
            //background

            fragColor = texture(iChannel1,rd);
            return;
        }

        rp += rd*d;
    }

    fragColor = texture(iChannel1,rd);
}
