vec2 rotate(vec2 vec, float angle) {
    // Thanks, FabriceNeyret2 :)
    float c, s;
	return mat2(c=cos(angle), s=sin(angle), -s, c) * vec;
}    

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tex = texture(iChannel0, uv * vec2(1, 1)).rgba;
    float luma = tex.r * 0.21 + tex.g * 0.72 + tex.b * 0.07;
    float dith1 = rotate(fragCoord, 0.3).y / 20.0;
    luma = step(1.0 - luma, mod(dith1, 1.0));
    fragColor = vec4(luma);
}
