#define iMax 1024.

vec3 julia(vec2 z)
{
	vec2 c = vec2(-0.55,0.55)*(cos(iTime*0.2)*0.1+1.0);
    //c = vec2(-0.8,0.2)*(cos(iTime*0.2)*0.1+1.0);
	float j = 0.0;
	for(float i = 0.0; i<iMax; i++)
	{
		z = vec2(z.x*z.x-z.y*z.y, z.x*z.y+z.x*z.y) + c;
		j = i;
		if(length(z) > 2.0) break;
	}
	return vec3(j/64.0,j/32.0,j/16.0);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
	uv.x*=1.78;
	uv*=sin(iTime*0.2)+1.5;
	float ang = iTime*0.1;
	mat2 rot = mat2(cos(ang),-sin(ang),sin(ang),cos(ang)); 
	uv*=rot;
	vec3 col = julia(uv);
	fragColor = vec4(col,1.0);
}
