void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);

    uv *= 12.;

    float cm = .2+pow(texture(iChannel0,vec2(0.2,.0)).x,1.)*.6;

    for (int i = 1; i < 5; i++) {
        float fi = float(i);
        uv += vec2(cos(uv.x+fi)*fi,sin(uv.y+fi*uv.x)*fi)*cm;
        uv = cos(uv.yx);
    }


	fragColor = vec4(1.-length(max(abs(uv)-vec2(.4),0.)));
}
