float circle(in vec2 st, in float radius){
    vec2 centerOfScreen = vec2(0.5);
    vec2 vecToPixel = st - centerOfScreen;
	return 1.-smoothstep(radius-(radius*0.01), radius+(radius*0.01), length(vecToPixel)*2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
	vec2 uv = fragCoord.xy / iResolution.xy;

    float border = (iResolution.x - 5.0) / iResolution.x;

    float circleVal = circle(uv,1.0);
    float borderVal = circle(uv,border);

	vec3 color = vec3(0.0, circleVal, 0.0);
    float dist = dot(uv - vec2(0.5), uv - vec2(0.5));

    color -= vec3(borderVal);// / (0.85 + dist);

	fragColor = vec4(color, 1.0);
}
