// Click to change the effect

vec2 rotate(vec2 p, float ang){
    float c = cos(ang), s = sin(ang);
    return vec2(p.y * c - p.x * s, p.y * s + p.x * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = (4.0*( fragCoord.xy / iResolution.xy ) - 2.0);


  	vec2 m = iMouse.xy / iResolution.xy;
	vec2 F=rotate(gl_FragCoord.xy,iTime/10.0);

  	vec3 p = vec3(F / iResolution.y, 0.5-m.x+0.02*sin(iTime));
	float ki=16.0+112.0*(iMouse.x/iResolution.x);
  	for (float i = 1.0; i < 128.0; i++)
	{
		if(i>ki)
			break;
    		p.xzy = vec3(1.3, 0.999, 0.777) * abs(p/dot(p,p)-vec3(1.0,1.0,m.y*0.3));
	}

	float cm=0.0;

	float cre=R.x;
	float cim=R.y;
	float zre=p.x/5.0;
	float zim=p.y/5.0;
	float max=32.0;
	float j=0.0;

	for(float i=0.0;i<256.0;i++)
	{
		if(i>max)
		{
			cm=0.0;
			break;
		}
		float dam=zre*zre-zim*zim+cre;
		zim=2.0*zre*zim+cim;
		zre=dam;
		if( (zre*zre+zim*zim)>4.0)
		{
			cm=i/max;
			break;
		}
	}

	vec3 cc=vec3(cm,cm,cm)*p;

	if(cm<0.3)
		cc = vec3(cm,cm,cm)/p;


    fragColor = vec4(cc,1.0);
}
