#define NUM_DRIPS 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
;
    float fStep = 123.456;
    float fHeight = 0.0;

    for (int i = 0; i < NUM_DRIPS; i++)
    {

        vec2 center = vec2 (0.5, 0.5);
		float t = iTime * 0.3;
    	float fAge = fract(t);

        vec2 vR = vec2 (uv - center);
        float R = sqrt (dot (vR, vR));

        float range = 10.0 * (R - fAge);
        float fRange =  0.3 - clamp(range,0.0,0.3);

        fHeight +=
            1.0
            //(1.0 - fAge) 
            * fRange
            * sin (R*29.0 - fAge*8.0);
    }

    uv.y -=  fHeight*fHeight * sign(fHeight);
    vec4 vTex = texture (iChannel0, uv);

    fragColor = vTex; 
}
