void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = mod(iTime,20.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0+2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    vec2 mouse = iMouse.xy/iResolution.xy;

    float pix = (mouse.x+0.01)*64.0;

    p = floor(p*pix)/pix;

    vec3 col = vec3(0.0);

    col = mix(vec3(0.9),vec3(0.0),smoothstep(0.5,0.55,length(p)));
    col *= 1.0-0.3*length(p-vec2(0.1,0.2));

    vec2 pp = p;
    pp.y += -0.3;
    pp.x += -1.0+time*0.1;

    col = mix(vec3(0.0),col,smoothstep(0.45,0.5,length(pp)));

	fragColor = vec4(col,1.0);
}
