void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(0., 0., 0., 1.0);
}

#define PI 3.1415926535897932384626433832795

float vol = 0.05;
float bpm = 150.;

float n2f(float n) {
    return 155.56 * pow(pow(2., 1./12.), n);
}

float sinewave(float t, float f, float a) {
    return sin(2.*PI*f*t)*a;
}

float sawwave(float t, float f, float a) {
    return (mod(t, 1./f) * f - 0.5) * 2. * a;
}

void play(float w, out float s, out float d, float t, float n) {
    if(t >= d && t < d+n) {
        s += w;
    }
    d += n;
}

void skip(out float d, float n) {
    d += n;
}

void triplet(float t, out float s, out float d) {
    play(sawwave(t, n2f(0.), vol), s, d, t, 8./bpm);
    skip(d, 4./bpm);
    play(sawwave(t, n2f(0.), vol), s, d, t, 8./bpm);
    skip(d, 4./bpm);
    play(sawwave(t, n2f(0.), vol), s, d, t, 8./bpm);
    skip(d, 16./bpm);
}

vec2 mainSound( in int samp,float time) {
    //float vol = 0.05;
    float s1 = 0., s2 = 0., s3 = 0., s4 = 0.;
    float d1 = 0., d2 = 0., d3 = 0., d4 = 0.;
    //float bpm = 150.;
    //time += 9.;

    /* Channel 1 */
    for(float i = 0.; i < 4.; i++) {
        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 20./bpm);
        skip(d1, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 20./bpm);
        skip(d1, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 20./bpm);
        skip(d1, 4./bpm);

        play(sawwave(time, n2f(12.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(19.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s1, d1, time, 48./bpm);

        play(sawwave(time, n2f(14.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(17.),     vol),    s1, d1, time, 48./bpm);

        play(sawwave(time, n2f(13.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(17.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(16.),     vol),    s1, d1, time, 48./bpm);
    }

    for(float j = 0.; j < 4.; j++) {
        for(float i = 0.; i < 12.; i++) {
            triplet(time, s1, d1);
        }

        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(2.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(4.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(9.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(13.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(12.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(8.),     vol),    s1, d1, time, 24./bpm);
        play(sawwave(time, n2f(7.),     vol),    s1, d1, time, 24./bpm);
    }

    for(float i = 0.; i < 7.; i++) {
        play(sawwave(time, n2f(5.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(3.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(2.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(6.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(4.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(3.),     vol),    s1, d1, time, 12./bpm);
        play(sawwave(time, n2f(0.),     vol),    s1, d1, time, 12./bpm);
    }

    /* Channel 2 */
    for(float i = 0.; i < 2.; i++) {
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);

        play(sawwave(time, n2f(12.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(19.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s2, d2, time, 48./bpm);

        play(sawwave(time, n2f(14.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(17.),     vol),    s2, d2, time, 48./bpm);

        play(sawwave(time, n2f(13.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(17.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(16.),     vol),    s2, d2, time, 48./bpm);
    }

    for(float i = 0.; i < 2.; i++) {
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 20./bpm);
        skip(d2, 4./bpm);

        play(sawwave(time, n2f(12. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(19. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18. + 5.),     vol),    s2, d2, time, 48./bpm);

        play(sawwave(time, n2f(14. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(18. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(17. + 5.),     vol),    s2, d2, time, 48./bpm);

        play(sawwave(time, n2f(13. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(17. + 5.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(16. + 5.),     vol),    s2, d2, time, 48./bpm);
    }

    for(float j = 0.; j < 4.; j++) {
        for(float i = 0.; i < 12.; i++) {
            triplet(time, s2, d2);
        }

        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(2.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(4.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(9.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(13.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(12.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(8.),     vol),    s2, d2, time, 24./bpm);
        play(sawwave(time, n2f(7.),     vol),    s2, d2, time, 24./bpm);
    }

    for(float i = 0.; i < 7.; i++) {
        play(sawwave(time, n2f(5.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(3.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(2.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(6.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(4.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(3.),     vol),    s2, d2, time, 12./bpm);
        play(sawwave(time, n2f(0.),     vol),    s2, d2, time, 12./bpm);
    }

    //return vec2(s1 * 0.75 + s2 * 0.25, s1 * 0.25 + s2 * 0.75);
    return vec2(s1, s2);
}
