// Escape your inner demon...thing... by start screaming in your microphone :D

float bw(vec2 coords) { vec4 lm = texture(iChannel0, coords) * vec4(0.21, 0.71, 0.07, 1);	return (lm.r + lm.g + lm.b) * 1.2; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x = 1.0 - uv.x; // mirror image, thx @porglezomp

	vec2 of = vec2(1.0 / 128.0, 0);

    float i = vec4(texture(iChannel1, fragCoord.xy * 50.0) * 1.0).y;

	float bwColor = sqrt(
		pow(abs(bw((uv * i) * 2.0) - bw(uv+of.xx)), 2.0) +
		pow(abs(bw((uv * of.xy) * .5) - bw(uv + of.yx+i)), i * 25.1)
	);

	fragColor = (vec4(bwColor, bwColor, bwColor, 2.5));
}
