vec4 fBm(vec2 p) {
    return texture(iChannel0, p);
}

vec4 fBm2(vec2 p) {
    return texture(iChannel1, p);
}

 vec4 fBm3(vec2 p) {
    return texture(iChannel2, p);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 fragColor2 =   fBm(uv.x*vec2(iTime* 0.01));
    vec4 fragColor3 = fBm2((uv.xy*vec2(0.1)*uv.y) + vec2(iTime*0.005, 0.1)) + vec4(0.2, 0.4, 0.1, 0.4);
    vec4 fragColor4 = fBm3((uv.xy*vec2(0.1)*uv.x) + vec2(iTime*0.05, 0.1)) + vec4(0.2, 0.3, 0.1, 0.3);
    vec4 fragColor5 = fBm2((uv.xy*vec2(0.1)*uv.y) + vec2(iTime*0.01, 0.1)) + vec4(0.2, 0.2, 0.1, 0.2);

    fragColor = fragColor2 + ( ( fragColor3+ fragColor4 )*fragColor5 )  + vec4(iTime * 0.5,0,0,iTime * 0.5) ;




;
