void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float x = (uv.x-0.5);
    float y = (uv.y-0.5);
    x += sin(y*5711.)/70.; // the magick!
    vec4 tex = texture(iChannel0, uv, 0.);

    y *= sin(x*60.);
    x *= 1.+sin(y*(40.+sin(iTime*0.25)*20.));

    y *= (iResolution.y/iResolution.x);
    float r;
    r = (sin(iTime*1.)+1.)/2. *0.2+0.2;
    r = sqrt(x*x+y*y)*2./r;
    float br = r*1.;
    float e = 0.5;
    float f = 5.;
    br = 1./( e+ ((br<e)?(e-br):(br-e)) *f);
    fragColor = vec4(0.4, ((sin(iTime*2.)+1.)/2.)*0.1+2., 0.9, 1.)*br;
}
