void mainImage(out vec4 o, vec2 n) {
    float t = iTime,s = 8.+5.*sin(t);
    vec2 m = iMouse.xy;
    if(length(n-(iResolution.xy-m))>s+s && length(n-m)>s)
        discard;
    o = clamp(abs(fract(t + vec4(1, .6, .3, 0)) * 6. - 3.) - 1., 0., 1.); }

