renderpass.code:
vec2 cn_square(vec2 cn){
    return vec2(cn.x * cn.x - cn.y * cn.y, 2.*cn.x*cn.y);
}

vec2 cn_add(vec2 a, vec2 b){
    return vec2(a.x + b.x, a.y + b.y);
}

vec2 mandelbrot(vec2 x, vec2 c){
    return cn_add(cn_square(x), c);
}

vec2 mIterate(vec2 x, vec2 c, int iter){
    vec2 result = x;
    for(int i = 0; i < 70;i++){
     	result = mandelbrot(result, c);   
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x / iResolution.y;
    vec2 o = vec2(0.85,0.5);
    vec2 mousePos = vec2(iMouse.x / iResolution.x * 1.8, iMouse.y / iResolution.y) - o;
    mousePos += vec2(cos(iTime*5.), sin(iTime*5.))*0.02;
    float point = 0.;
    if(length(-mousePos + p - o) < 0.01){
        point = 1.;
    }

    vec2 julia = mIterate((p-o)*03., mousePos, 7);
    vec2 mandelbrot = mIterate(vec2(0, 0), (p-o), 7);
    //vec4 camera =  texture(iChannel1, p);

	fragColor = vec4(length(mandelbrot)-0.5, length(julia)*2., point, 1.);
}
