// Original: https://www.shadertoy.com/view/4tl3W8 by nimitz

// Sound response + mouse input: ChazMeister

float squared(float value) { return value * value; }

float getAmp(float frequency) { return texture(iChannel0, vec2(frequency / 512.0, 0)).x; }

float getWeight(float f) {
    return (+ getAmp(f-2.0) + getAmp(f-1.0) + getAmp(f+2.0) + getAmp(f+1.0) + getAmp(f)) / 5.0; }

float getPulse(float i, float mult) {
    return (getWeight(squared(i)) * mult);
}

void mainImage( out vec4 f, in vec2 w ){
	vec4 p = vec4(w, 0., 1.) / iResolution.y - vec4(.9, .5, 0, 0), c = p - p;
	float t = iTime,r = length(p.xy += sin(t + sin(t * .8)) * .4), a = atan(p.y, p.x);
	for (float i = 0.;i<60.;i++)
        c = c * .98 + getPulse(i, 0.3) / 4.0 + (sin(i + vec4(5,3,2,1)) * .5 + .5 * (getWeight(squared(i) * 20.0) * 2.5)) * smoothstep(.99, 1., sin(log(r + i * .05) - t - i + sin(a += t * .01) * iMouse.y / 200.0));
    f = c * r;
}
