void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float sx=iResolution.x/iResolution.y;
    vec2 s;

    uv.y=uv.y/sx;


    //follow the mouse
    //s=iMouse.xy/iResolution.xy;

    //animation
    //s.x=abs(sin(iTime));
    //s.y=abs(sin(iTime));

    //fixed position
    s.x=0.5;
    s.y=0.3;

    if(length(uv-s)<0.2){
        fragColor = vec4(uv,abs(sin(iTime)),1.0);
    }else if(length(uv-s)<0.25){
        fragColor = vec4(1.0-uv.x,1.0-uv.y,abs(sin(iTime)),1.0);
    }else{
        fragColor = vec4(0,0,0,1.0);
    }
}
