void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = (uv-0.5)*2.0;
    uv = abs(uv);

    vec3 color = vec3(0);

    float dis = length(uv);
    float disX = abs(uv.x);
    float disY = abs(uv.y);

    float p = 2.;
    float d = pow(disX,p) + pow(disY,p) + pow(dis,p);

    color = vec3(pow(disX,p), pow(disY,p), pow(dis,p));

	fragColor = vec4(color, 1);
}
