void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define t iTime/1000.0
    vec4 solidBlack = vec4(0,0.0,0.0,1.0);
    vec2 xy = fragCoord;
    xy = xy / iResolution.xy - 0.5;
    float x = xy.x, y = xy.y, x4 = x*x*x*x, x2y2 = x*x*y*y, y4 = y*y*y*y, xxy = x*(x*x-y*y);
    solidBlack.r = (x4+x2y2+y4-xxy) * t * 1000.0;
    solidBlack.g = (x4+x2y2+y4+xxy) * t * 1000.0;
    solidBlack.b = (x4-x2y2+y4-xxy) * t * 1000.0;
    fragColor = solidBlack;
}
