void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy -iResolution.xy)/iResolution.y;
	float L = length(uv)*4.0;
    float K = atan (uv.y,uv.x)+iTime;
    float X = (mod(sin(K*3.0),cos(K*3.0)));
    float Y = (mod(cos(K*3.0),sin(K*3.0)));
    vec3 A = vec3(X,X,1.0-X);
    vec3 B = vec3(Y+1.0,Y+2.0,Y+5.0);
    vec3 T = 1.0-vec3(sin(iTime)*L,cos(iTime)*L,cos(iTime)*L);
    vec3 color = vec3(0.9-smoothstep(A,B,T));
	fragColor = vec4(vec3(0.9,0.9,0.5)*color,1.0);
}
