void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //vec2 bottomleft = vec2(-.1, .7);
    //vec2 topright = vec2(.1, .9);

    vec2 center = vec2(-.03, .72);

    vec2 diagonal = vec2(1, 1);

    float scale = iTime*iTime;

    vec2 bottomleft = center - diagonal/scale;
    vec2 topright = center + diagonal/scale;

    vec2 range = topright - bottomleft;
    uv = uv*range + bottomleft;

    vec2 z = vec2(0.0, 0.0);
    vec2 c = uv;

    bool in_set = true;
    for (int i = 0; i < int(100); i++) {
        if (length(z) > 2.0) {
        	in_set = false;   
            break;
        }
        vec2 zsquared = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y);
        z = zsquared + c;
    }


    fragColor = vec4(vec3(in_set ? 1.0 : 0.0), 1.0);
}
