const float PI = atan(1.0) * 4.0;
const float TAU = atan(1.0) * 8.0;

const float rad60 = (atan(1.0) * 4.0) / 3.0;
const float rad120 = (atan(1.0) * 4.0) / 1.5;
const vec2 vec000 = vec2(1.0, 0.0);
const vec2 vec060 = vec2(cos(rad60), sin(rad60));
const vec2 vec120 = vec2(cos(rad120), sin(rad120));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
    float scale = 10.0;
    scale *= (cos(iTime * TAU / 8.0) + 1.0);

    fragColor = vec4(0.0);

    float polkadotMask = 0.0;

    polkadotMask += cos(abs(dot(pos, vec000)) * scale * TAU);
    polkadotMask += cos(abs(dot(pos, vec060)) * scale * TAU);
    polkadotMask += cos(abs(dot(pos, vec120)) * scale * TAU);


    fragColor.rg = polkadotMask * mouse;
}
