//Es posible agregar interactividad ocupando el mouse. La posicion del mouse
//viene en la variavle iMouse. En este ejemplo la posicion del circulo rojo cambiar 
//al clickear con el mouse en otra ubicaci√≥n
struct Circle {
  vec2 position;
  float radius;
  vec4 color;
};

bool testCircle(Circle c, vec2 pixelCoords)
{
  return distance(c.position,pixelCoords) < c.radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = iMouse.xy/ iResolution.xy;

    Circle circles[3];
	circles[0] = Circle(mouse, 0.1, vec4(1.0,0.0,0.0,1.0));
    circles[1] = Circle(vec2(0.2,0.5), 0.1, vec4(0.0,1.0,0.0,1.0));
    circles[2] = Circle(vec2(0.7,0.7), 0.1, vec4(0.0,0.0,1.0,1.0));
    fragColor = vec4(1.0);  
    for(int i=0; i<3; i++)
  	{
    	if(testCircle(circles[i], uv))
        	fragColor = circles[i].color;
    }
}
