vec4 BlurH (sampler2D source, vec2 size, vec2 uv, float radius) {

	if (radius >= 1.0)
	{
		vec4 C = vec4(0.0); 

		float width = 1.0 / size.x;

		float divisor = 0.0; 

		for (float x = -20.0; x <= 20.0; x++)
		{
			C += texture(source, uv + vec2(x * width, 0.0));
			divisor++; 
		}

		return vec4(C.r / divisor, C.g / divisor, C.b / divisor, 1.0);
	}

	return texture(source, uv);
}

vec4 BlurV (sampler2D source, vec2 size, vec2 uv, float radius) {

	if (radius >= 1.0)
	{
		vec4 C = vec4(0.0); 

		float height = 1.0 / size.y;

		float divisor = 0.0; 

       for (float y = -20.0; y <= 20.0; y++)
		{
			C += texture(source, uv + vec2(0.0, y * height));
			divisor++; 
		}

		return vec4(C.r / divisor, C.g / divisor, C.b / divisor, 1.0);
	}

	return texture(source, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    	// in practice, you save the first pass into an
    	// intermediate texture and render the V pass
    	// separately

	vec4 A = BlurH(iChannel0, iResolution.xy, uv, 6.0);	
         //A += BlurV(iChannel0, iResolution.xy, uv, 6.0); 
    	 //A *= 0.5;

	fragColor = A;
}
