#define t (iTime+1234.5)
#define r iResolution
#define w(a,b,c,d,e) abs(sin(a*sin(b*t)*sin(c*t)+dot(uv,vec2(cos(d*t),sin(d*t)))*(sin(e*t)+15.0)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / r.xy;
    uv.x *= r.x/r.y;
	fragColor = vec4(
        w(17.,.2,.3,.07,.4),
        w(13.,.5,.2,.05,.5),
        w(11.,.7,.5,.03,.3), 1.);
}
