void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t= iDate.w, g = 1.;
    vec2 p = fragCoord.xy/iResolution.y;
    p. x -= 0.9;
    vec3 o=vec3(0.,0,-4),
    d=normalize(vec3(p,2.5));
/*
    float  a=cos(t),b=sin(t);
    mat2 R=mat2(a,b,-b,a);
    o.yz*=R;
    d.yz*=R;
*/
    for(int i=0;i< 20;i++)
    {
        g = min(g, t);
        o+=d*(t=length( vec2(length(o.xy) - 1., o.z))- .2);
    }
    vec3 c1= texture(iChannel0,vec2(atan(p.y  ,p.x  )  )).rgb * length(p * 9.) ;
    float of = texture( iChannel1, vec2( .01, .25 ) ).x;
    of *= c1.r  * .2  ;
    p +=of; 
    c1 *= texture(iChannel0, p + iDate.w * .4).rgb ;    

 //   c1 = t<1.? cos(d + c1 ): c1;    
    c1 = t<1.? sin(c1 ) : c1;      

    fragColor.rgb = mix(vec3(1) - c1, c1, g/of);
}
