// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.


vec2 z1 = vec2(0.2,0.0);

vec2 csqr( vec2 a ) { return vec2(a.x*a.x-a.y*a.y, 2.0*a.x*a.y ); }

vec2 map(vec2 p){
	vec2 z = p;
	float f = 4.;
	float g = 4.;    

	for( int i=0; i<32; i++ ) 
	{	   
		z = csqr(z)+p;
		f = min( f, abs(dot(z-p,z+p)));
		g = min( g, abs(dot(z-1.05*p,z+1.05*p)));
	}
    return vec2(f,g);
}

vec2 fbm(vec2 z) {
	vec2 total = vec2(0.0);
    float amplitude = 1.0;
	for (int i = 0; i < 10; i++) {
		total += map(z) * amplitude;
		z =(z-z1)* 1.5+z1;
		amplitude *= 0.3;
	}
	return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = .8*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
	p=fbm(p);	
	float f = 1.0+log(p.x)/15.0;
	float g = 1.0+log(p.y)/15.0;

	fragColor =  1.-abs(vec4(g*g,f*f,f*f*f,1.0));
}
