const float pi = 3.1415926535897932384626433832795;
const float flashDuration = 3.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = mod(iTime, 5.0) / 5.0;

    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    vec2 circlePos = vec2(0.5*aspect, 0.5);

    vec2 thing = normalize(uv - circlePos);

    float T = mod(iTime, flashDuration);
    float deg  = mix(90.0, 270.0, cos(pi/flashDuration*T - pi)/2.0+0.5);
    float xreq = cos(radians(deg));
    float yreq = sin(radians(deg));

    if (pow(circlePos.x - uv.x, 2.0) + pow(circlePos.y - uv.y, 2.0) > 0.1) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    } else if (thing.y > yreq) {
		fragColor = vec4(0.3, 0.3, 0.3, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
