vec3 drawRect(float x, float y, float sizeX, float sizeY, vec2 st, vec3 color)
{    
    float left = step(x,st.x);
    float bot = step(y, st.y);
    float right = 1. - step(x + sizeX,st.x);
    float up = 1. - step(y + sizeY, st.y);

    float left2 = step(x - 0.02,st.x);
    float bot2 = step(y - 0.02, st.y);
    float right2 = 1. - step(x + sizeX + 0.02,st.x);
    float up2 = 1. - step(y + sizeY + 0.02, st.y);


    return vec3(1. - (left2 * bot2 * right2 * up2)) + 
        		vec3(left * bot * right * up) * color;
}

vec3 PietRed = vec3(0.709, 0.07, 0.03);
vec3 PietWhite = vec3(0.95, 0.92, 0.9);
vec3 PietYellow = vec3(0.923, 0.92, 0.15);
vec3 PietBlue = vec3(0.0, 0.02, 0.65);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec3 rect = drawRect(0.2, 0.25, 1., 1., st, PietRed);
    vec3 rect2 = drawRect(0.2, 0.0, 0.7, 0.23, st, PietWhite);
    vec3 rect3 = drawRect(0.925, 0.0, 0.7, 0.10, st, PietYellow);
    vec3 rect4 = drawRect(0.0, 0.0, 0.18, 0.23, st, PietBlue);
    vec3 rect5 = drawRect(0.0, 0.25, 0.18, 0.4, st, PietWhite);

    color = rect * rect2 * rect3 * rect4 * rect5;

    fragColor = vec4(color,1.0);
}
