#define time iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 pos = ( fragCoord.xy / iResolution.xy )*8.-vec2(4.,4.);// + mouse / 4.0;

    if(pos.y>-6.){
		//pos.y += 0.1*sin(time*3.)+0.13*cos(time*2.+0.6)+.1*sin(time*3.+0.4)+0.2*fract(sin(time*400.));
        pos.y += 0.2*fract(sin(time*400.)+cos(time));
	}

	vec3 color = vec3(0.,0.,0.0);

	float p =.004;


	float y = pow(abs(pos.x),3.2)/(2.*p)*3.3;


	float dir = length(pos+vec2(pos.x,y))*sin(0.26);//*(0.01*sin(time)+0.07);

	if(dir < 0.7){
		color.rg += smoothstep(0.0,1.,.75-dir);
		color.g /=2.4;
	}
	color += pow(color.r,1.1);
    //new vertion
    /*
        float p1 = .015;
        float y1 = pow(abs(pos.x),3.2)/(2.*p1)*3.3;
        float d = length(pos+vec2(pos.x,y1+2.3))*sin(0.28);
        color.b += smoothstep(-0.2,.9,0.47-d);
        color.g += smoothstep(-0.3,.9,0.17-d);
    */
	fragColor = vec4(color,1.0);
}
