//Variables for number of squares 
float numSquaresX = 8.0;
float numSquaresY = 5.0;

//Set to true to see textures, or false for black&white
bool useTextures = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 uv = fragCoord.xy / iResolution.xy; 

    //The Colors and Textures
    vec4 texture1 = texture(iChannel0, uv);
    vec4 texture2 = texture(iChannel1, uv);
    vec4 whiteTiles = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 blackTiles = vec4(0.0, 0.0, 0.0, 0.0);    

    //set uv to be the number of tiles and use floor to convert it to a pseudo int
	uv.x = floor(uv.x*numSquaresX);
    uv.y = floor(uv.y*numSquaresY);


    //Allows use of both black and white, or textures
    if(useTextures == true)
    {
        //Does the math to make the checkerboard
        if (mod(uv.x, 2.0) == mod(uv.y, 2.0))
			fragColor = texture2;
    	else 
        	fragColor = texture1;
    }
    else
    {
        //Does the math to make the checkerboard
        if (mod(uv.x, 2.0) == mod(uv.y, 2.0))
			fragColor = blackTiles;
    	else 
        	fragColor = whiteTiles; 
    }
}
