void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float x = uv.x;
    float y = 1.0 - uv.y;

    // this doesn't give the exact same values as others.
    float val = floor(x*40.0) / 39.0;

    if (y > 6.0 / 7.0) {
        fragColor = vec4(val, val, val, 1.0);
    }
    else if (y > 5.0 / 7.0) {
        fragColor = vec4(0.0, val, val, 1.0);
    }
    else if (y > 4.0 / 7.0) {
        fragColor = vec4(val, 0.0, val, 1.0);
    }
    else if (y > 3.0 / 7.0) {
        fragColor = vec4(0.0, 0.0, val, 1.0);

    }
    else if (y > 2.0 / 7.0) {
        fragColor = vec4(val, val, 0.0, 1.0);
    }
    else if (y > 1.0 / 7.0) {
        fragColor = vec4(0.0, val, 0.0, 1.0);
    }
    else  {
        fragColor = vec4(val, 0.0, 0.0, 1.0);
    }
}
