vec2 housePoint = vec2(0.4, 0.0);
vec2 chimneyPoint = vec2(0.4, 0.6);
vec2 windowPoint1 = vec2(0.5, 0.35);

vec3 houseColor = vec3 (0.0, 0.0, 1.0);
vec3 chimneyColor = vec3 (1.0, 0.0, 0.0);
vec3 windowColor = vec3(0.0, 1.0, 0.0);

vec4 BG = vec4(1.0, 1.0, 1.0, 1.0);

float houseOn = 1.0;
float chimneyOn = 1.0;
float windowOn = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float houseL = housePoint.x + 1.0;
	float houseH = housePoint.y + 0.6;
    float chL = chimneyPoint.x + 0.1;
    float chH = chimneyPoint.y + 0.15;
    float win1L = windowPoint1.x + 0.25;
    float win1H = windowPoint1.y + 0.15;

    vec4 LayerBG = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 LayerHouse;
    vec4 LayerChimney;
    vec4 LayerWindow;

	vec2 uv = fragCoord.xy / iResolution.xy;    
    uv.x *= (iResolution.x/iResolution.y);
	//--------HOUSE-----------
    float house = step(housePoint.x, uv.x)*step(uv.x, houseL)*step(housePoint.y, uv.y)*step(uv.y, houseH);    
    LayerHouse = vec4(houseColor, 1.0*house*houseOn);
    //--------CHIMNEY---------
    float chimney = step(chimneyPoint.x, uv.x)*step(uv.x, chL)*step(chimneyPoint.y, uv.y)*step(uv.y, chH);
	LayerChimney = vec4(chimneyColor, 1.0*chimney*chimneyOn);
    //--------WINDOWS---------
    float window = step(windowPoint1.x, uv.x)*step(uv.x, win1L)*step(windowPoint1.y, uv.y)*step(uv.y, win1H);    
    LayerWindow = vec4(windowColor, 1.0*window*windowOn);

    window = step(windowPoint1.x+0.55, uv.x)*step(uv.x, win1L+0.55)*step(windowPoint1.y, uv.y)*step(uv.y, win1H);    
    LayerWindow += vec4(windowColor, 1.0*window*windowOn);

    window = step(windowPoint1.x, uv.x)*step(uv.x, win1L)*step(windowPoint1.y-0.25, uv.y)*step(uv.y, win1H-0.25);    
    LayerWindow += vec4(windowColor, 1.0*window*windowOn);

    window = step(windowPoint1.x+0.55, uv.x)*step(uv.x, win1L+0.55)*step(windowPoint1.y-0.25, uv.y)*step(uv.y, win1H-0.25);    
    LayerWindow += vec4(windowColor, 1.0*window*windowOn);

    //--------OUTPUT-------
    vec4 Output = mix(LayerBG, LayerHouse, LayerHouse.a);
    Output = mix(Output, LayerChimney, LayerChimney.a);
    Output = mix(Output, LayerWindow, LayerWindow.a);

    fragColor = Output;
}
