#define r(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
	     U = r(iTime) * ( u+u - R ) / R.y,
         S = sign(U) * r(.1);  // wave train direction ( 4 sectors, tilted by .1 or .28 ). 

    O = vec4( sin( 8.* log( dot( U, S ) ) + atan(S.y,S.x) -10.*iTime) );
	O = smoothstep( .7, .5, abs(O) );                                       // bands
  //O = smoothstep( 1.,0.,(abs(O)-.6)/fwidth(O) );                          // better antialiasing
  //O = sqrt(smoothstep( 0., 50./R.y/(abs(U.x) + abs(U.y)), .7 - abs(O) )); // Shane variant
}
