//"Wire" by Xor - 2017

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float X = fragCoord.x*4./iResolution.y+iTime+.5*cos(iTime);
	vec3 Height = vec3(cos(X+cos(fragCoord.x))*.2+.2*sin(fragCoord.x)+.5,
                       cos(X+cos(fragCoord.x+2.))*.2+.21*sin(fragCoord.x+2.)+.5,
                       cos(X+cos(fragCoord.x+4.))*.2+.22*sin(fragCoord.x+4.)+.5);
    vec3 Dist = abs(Height*iResolution.y-fragCoord.y);
	fragColor = vec4(step(Dist,vec3(.5))+pow(Dist/iResolution.y*.4,vec3(2)),1);
}

