#define c30 0.86602540378
#define hm  (4.0/3.0)
#define grid 50.0
#define constRes 900.0
#define smooth (1.0 / constRes * grid)
#define timeScale 1.0
#define lThick 0.01
#define lKnob 0.1
#define rt (iTime * timeScale)
#define SM(v) smoothstep(smooth,-smooth, v)


vec2 hex(vec2 v){
	float yc = abs(fract((v.x + floor((v.y*hm + 0.5))*(c30 / 2.0))/c30) - 0.5);
	float y = floor(v.y*hm + yc);
	float x = floor(v.x/c30 + 0.5 + fract(y / 2.0))- fract(y / 2.0);
	return vec2(x*c30,y /hm + 1.0/(hm * 4.0));
}

float rand(vec2 v){
    return fract(sin(dot(v,vec2(5.11543,71.3132)))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy  / constRes;

    vec2 c = hex(uv * grid);
    float dist = length(uv*grid - c);
    float nos = rand(c);
    float nx = (nos + 0.6) * .2;
    float line = SM(abs(dist - nx) - lThick);
    float rrt = rt * (rand(vec2(nos)) + 1.0) + nos * 300.0;
    float knob = length(uv * grid - c + nx * vec2(cos(rrt),sin(rrt)));
    knob = SM(knob - lKnob);
    float finCol = 1.0 - (knob + line);
	fragColor = vec4(finCol);
}
