void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = vec2(fragCoord.xy / iResolution.xy);

    float period = 2.0;
    float nImages = 100.0;

    vec3 ret;

    float modtime = mod(iTime / period, 1.0); // runs from 0 to 1 in [period] seconds

    // factor should be from 1 to [max repeated images]

    // LINEAR ZOOM does't look as good
    // float factor = (nImages - 1.0) * modtime + 1.0;

    // EXP ZOOM
    float factor = exp(log(nImages) * modtime);

    vec2 fetch = (p-.5) * factor+.5;

    vec3 texA = texture(iChannel0, fetch).xyz;

    ret = mix(texA, vec3(0.0), modtime);

	fragColor = vec4(ret, 1.0);
}
