// Python and OpenGL for Scientific Visualization
// 2017 Nicolas P. Rougier

// Union (A or B)
float csg_union(float d1, float d2)
{
    return min(d1,d2);
}

// Signed distance to a circle
float circle(vec2 p, vec2 center, float radius)
{
    return length(p-center) - radius;
}

// Blue or red color according to distance d
vec4 color(float d)
{
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 blue  = vec3(0.1, 0.4, 0.7);
    vec3 color = white - sign(d)*blue;
    color *= (1.0 - exp(-4.0*abs(d))) * (0.8 + 0.2*cos(140.0*d));
    color = mix(color, white, 1.0-smoothstep(0.0,0.02,abs(d)) );
    return vec4(color, 1.0);
}


// Move circles using mouse
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = max(iResolution.x, iResolution.y)/6.0;
    vec2 center1 = vec2(iResolution.x/2.0 - radius/2.0,
                        iResolution.y/2.0);
    vec2 center2 = vec2(iResolution.x/2.0 + radius/2.0,
                        iResolution.y/2.0);
    // Uncomment to move second circle with mouse
    // center2 = iMouse.xy;

    vec2 p = gl_FragCoord.xy;
    float d1 = circle(p, center1, radius);
    float d2 = circle(p, center2, radius);
    float d = csg_union(d1,d2);
    fragColor = color(-d/radius);
}
