// My entry for the "2 tweet" challenge 2015, organized by nimitz: https://www.shadertoy.com/view/4tl3W8.
//
// Raymarched, textured, lit and colored stuff, in less than 280 characters.
//
// function m() at line 16 is the animated geometry modeling, line 26 is the
// camera setup, lines 28 amd 29 are the raymarching/intersector and line 31
// is the shading (texturing, lighting and colored fog).

#define V vec3

V k = V(.4,-.2,.9);

V m( V p )
{
    p -= iTime;
	for( int i=0; i<16; i++ ) 
        p = reflect( abs(p)-9., k );
    return p* .5;
}

void mainImage( out vec4 c, in vec2 p )
{
    V d = V(p,1)/iResolution, o = d;

    for( int i=0; i<99; i++ ) 
        o += d * m(o).x;

    c = texture( iChannel0, m(o).yz ) * (.5 + 99.*m(o-k*.02).x) * exp(.04*o.yzzz);
}
