void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //Generate a uv offset describing a vector from
    //the center of the screen
    vec2 pos = ( fragCoord.xy/iResolution.xy ) - vec2(0.5, 0.5);

    //Correct aspect ratio
    pos.x *= iResolution.x/iResolution.y;

    //Get the distance from the current pixel to the center
    float radius = length(pos);

    //Get the angle from the current pixel to the center
    float angle = atan(pos.y, pos.x);

    //Generate a sinusoidal intensity based off of the radius
    //These look like rings.
    float ringMask = pow(sin(radius * 32.0), 2.0);

    //Scale the intensity by the radius, to produce a dimming effect
    ringMask = ringMask*radius;

    //Create a rotating 'sun burst' from the center.
    float spinMask =  pow(sin(angle * 68.0 + 32.0*iTime) * 0.5 + 0.5, 2.0);

    //Multiply the intensities together to create dots from the center.
    float composition = spinMask * ringMask;

    //Modulus based pattern switching to demonstrate layers

    spinMask *= mod(iTime,3.0) < 1.0 ? 1.0 : 0.0;
    ringMask *= mod(iTime+1.0,3.0) < 1.0 ? 1.0 : 0.0;
    composition *= mod(iTime+2.0,3.0) < 1.0 ? 1.0 : 0.0;
    float c = spinMask + ringMask + composition;


    vec4 color = vec4(0.0, 0.0, 0.15, 0.0);


    color += vec4(2.0 * c, 2.0 * c, 2.0 * c, 1.0 * c);

    fragColor = color;
}
