// edit of http://glslsandbox.com/e#18752.0
uniform vec2 mouse;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float gTime = iTime*0.25;
	float f =20., g=0.005;
	vec2 res = iResolution.xy;
	vec2 mou = mouse.xy;

	//if (mouse.x < 0.5)
	//{
	mou.x = sin(gTime * .3)*sin(gTime * .17) * 1. + sin(gTime * .3);
	mou.y = (1.0-cos(gTime * .632))*sin(gTime * .131)*1.0+cos(gTime * .3);
	mou = (mou+1.0) * res;
	//}
	vec2 z = ((-res+2.0 * gl_FragCoord.xy) / res.y);
	vec2 p = ((-res+2.0+mou) / res.y);
	for( int i = 0; i < 5; i++) 
	{
		float d = dot(z,z);
        z = (vec2( dot(-z.x,0.5), dot(z.y,1.5) ) / d) + p; 
        z.x =  1.0-abs(z.x);
        z.y =  1.0-abs(z.y);
		f = max( f-d, (dot(z-p,z-p) ));
		g = min( g*d, sin(dot(z+p,z+p))+1.0);
	}
	f = abs(-log(f) / 3.5);
	g = abs(-log(g) / 8.0);
	fragColor = vec4(min(vec3(g, g*f, f), 1.0),1.0);   
}
