void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.yy;
    vec3 v_Position = vec3(uv,0);

    float u_Radius = 0.5;
    float u_Thickness = 0.06;
    vec4 u_Color = vec4(1);      
    vec3 u_Center = vec3(0);
	vec4 background = vec4(vec3(0.0, 0.0, 160.0)/255.0, 1.0);


    float w = u_Thickness/2.0f;
    u_Radius = u_Radius - w;
    vec3 xyz = v_Position - u_Center;
    float l = length(xyz);        
   	fragColor = u_Color;
    float a = smoothstep(w/2.0, 0.0, abs(l-u_Radius)-w);       
    fragColor.a = a;
    fragColor = mix(background,fragColor, fragColor.a );
}
