// tunnel code borrowed from https://www.shadertoy.com/view/Xslyzf
// Bananas added by Del.

vec4 Banana(vec2 p)
{
    const vec4 col1 = vec4(0.0,.1,.0,1.0);
    const vec4 col2 = vec4(0.9,0.8,0.5,1.0);
    vec2 uv = p.xy;
    vec2 d = uv*(3.0);
    d.x *= 5.0;
    d.y *= 1.8;
    d.x += sin(d.y + iTime*0.2)*1.2;
    d.y += sin(d.x + iTime*0.2)*1.2;
    float v1=length(0.5-fract(d.xy))+0.40;
    return mix(col2,col1,v1*v1*v1*v1)*1.5;
}


void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
	g = (g+g-f.xz)/f.y;
    float t = iTime * .25;
    g.y -= 0.28;
    float a = atan(g.x,g.y)/1.27;
    float d = max(max(abs(g.x),abs(g.y)), min(abs(g.x)+g.y, length(g)));
    vec2 k = vec2(a,1.5/d + t);
    f = Banana(k.xy*0.25)*clamp(d,0.0,1.0);
}
