void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);
    //Aspect ratio correction
    uv.x *= iResolution.x/iResolution.y;

    //Calculate the radius from the center.
    float radius = sqrt(uv.x*uv.x + uv.y*uv.y);

    //Apply a sin opreation to produce rings
    float intensity = pow(sin(radius*32.0) * 0.5 + 0.5, 2.0);
    //Set the final color
    fragColor = vec4(0.1*intensity, 0.1*intensity, 0.55*intensity, 1.0);

}
