float hash( in vec2 p ) 
{
    return fract(sin(p.x*15.32+p.y*35.78) * 43758.23);
}

vec2 hash2(vec2 p)
{
	return vec2(hash(p*.754),hash(1.5743*p.yx+4.5891))-.5;
}


vec2 add = vec2(1.0, 0.0);

vec2 noise2(vec2 x)
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix( hash2(p),          hash2(p + add.xy),f.x),
                    mix( hash2(p + add.yx), hash2(p + add.xx),f.x),f.y);

}


float dseg( vec2 ba, vec2 pa )
{	
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0., 1. );	
	return length( pa - ba*h );
}


vec2 delirium(vec2 x)
{
    vec2 r = vec2(2.);
    float a = 1.;
    float d=10.;
    for (int i = 0; i < 12; i++)
    {
        vec2 s= noise2(r*a+iTime)/a+.5;
        d=min(d,dseg(s,x-r));
        r +=s;      
        a*=.8;
    }
    return normalize(r-x)*d;
    //return (r-x)*d/dot(r-x,r-x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*fragCoord.xy / iResolution.yy;
    uv*=5.;
    vec2 p = delirium(uv);
    float c = length(p)*20.;
    vec3 col;
    col=vec3(0.6)*c;
	fragColor = vec4(col,1.0);
}
