vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
	return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float PI = 3.14159;
    float numArms = 2.0;
    float spiralAngle = PI/3.0;

    float cX = uv.x - 0.5;
    float cY = uv.y - 0.5;

    float newX = sqrt(cX*cX + cY*cY);
    float newY = atan(cX, cY);

    float color = 0.0;
    color += uv.x+cos(5.0*iTime);
	color += cos(2.0*numArms*(newX*sin(spiralAngle) + newY*cos(spiralAngle)) + iTime);

    vec3 hsvd = hsv2rgb_smooth(vec3(color, 1.0, 1.0));

	fragColor = vec4(hsvd ,1.0);
}
