const float CircleNum = 5.;
void mainImage(out vec4 o, vec2 p)
{
    vec2 c=iResolution.xy*.5;
    float r=c.y,a=r;// radius, initialize angle with a positive value, distance
    for(float i=1.;i<=CircleNum;++i){
        float time =(iDate.w)/pow(60.,3.-(i-1.));
        if (i == 1.)time = iDate.w/(3600.*12.);
        o+=vec4(clamp(1./(float(i)/6.)-abs(length(p-c)-r),0.,1.));
        o.b+= 0.2*(clamp(1./(float(i)/6.)-(length(p-c)-r),0.,1.)-clamp(1./(float(i)/6.)-abs(length(p-c)-r),0.,1.));
        c+=(r*=.5)*vec2(cos(a=time*2.*3.14159265359-(3.14159265359/2.)),sin(-a));
    }
}
