void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

	//variables
	vec2 Position = vec2(0.5,0.3);
	vec2 eclipsePosition = vec2(0.35, 0.3);
	float Radius = 0.2;
	float eclipseRadius = 0.15;
	vec3 Background = vec3(1.0,uv.y,0.0);
	vec3 sunColor = vec3(1.0,1.0,0.0);
	vec3 eclipseColor = vec3(1.0,uv.y,0.0);


	eclipsePosition.x *= (sin(iTime * 0.1) + 1.4);

	//Question 2
	vec4 layer1 = vec4(Background, 1.0);
	vec4 layer2 = vec4(sunColor, 1.0 - smoothstep(0.1, Radius, length(Position - uv)));
	vec4 layer3 = vec4(eclipseColor, 1.0 - step(eclipseRadius, length(eclipsePosition - uv)));

	vec4 Composite = mix(layer1, layer2, layer2.a);
	Composite = mix(Composite, layer3, layer3.a);
	fragColor = Composite;

}
