void mainImage(out vec4 k, vec2 p)
{
    const vec4 col1 = vec4(0.0,.1,.0,1.0);
    const vec4 col2 = vec4(0.9,0.8,0.5,1.0);
    vec2 uv = p.xy / iResolution.xy;

    float s = sin(iTime*0.5);
    vec2 d = uv*(3.0+s*0.1);
    d.x *= 5.0;
    d.y *= 0.8;

    d.x += sin(d.y + iTime*0.2)*1.2;
    d.y += sin(d.x + iTime*0.2)*1.2;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.40;
    k = mix(col2,col1,v1*v1*v1*v1)*1.5;
}
