#define FREQ 40.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 pink = vec4(255.0/255.0, 147.0/255.0, 217.0/255.0, 1.0);
    vec4 green = vec4(90.0/255.0, 150.0/255.0, 98.0/255.0, 1.0);
    vec4 teal = vec4(118.0/255.0, 252.0/255.0, 252.0/255.0, 1.0);

    vec4 pixel = pink; //main wave color

    if (mod(fragCoord.y - iTime * 50.0, FREQ) > sin(fragCoord.x * 100.0) + 25.0 || mod(fragCoord.y - iTime * 50.0, FREQ) < sin(fragCoord.x * 100.0) + 5.0 )
    {
      pixel = pink;
    } else {
      pixel = mix(teal, green, 1.1 - uv.y); //add a gradient for the other waves
    }

	fragColor = pixel; //return
}
